const { ethers } = require("ethers");
const fs = require("fs");
require("dotenv").config();

async function deployContracts() {
  console.log("ðŸš€ Deploying to Polygon Amoy...");

  try {
    // Setup provider and wallet from env
    const rpcUrl = process.env.POLYGON_AMOY_RPC_URL;
    if (!rpcUrl) throw new Error("POLYGON_AMOY_RPC_URL not set");
    const pkRaw = process.env.PRIVATE_KEY;
    if (!pkRaw) throw new Error("PRIVATE_KEY not set");
    const pk = pkRaw.startsWith('0x') ? pkRaw : `0x${pkRaw}`;
    const provider = new ethers.JsonRpcProvider(rpcUrl);
    const wallet = new ethers.Wallet(pk, provider);
    
    console.log("Deployer:", wallet.address);
    const balance = await provider.getBalance(wallet.address);
    console.log("Balance:", ethers.formatEther(balance), "MATIC");
    
    if (balance === 0n) {
      console.log("âŒ No MATIC for gas. Get test tokens from: https://faucet.polygon.technology/");
      process.exit(1);
    }

    // BondFactory contract bytecode and ABI (simplified)
    const factoryBytecode = "\
";

    // Deploy BondFactory
    console.log("Deploying BondFactory...");
    const factoryTx = {
      data: factoryBytecode,
      gasLimit: 3000000,
      gasPrice: ethers.parseUnits("50", "gwei")
    };

    const deployTx = await wallet.sendTransaction(factoryTx);
    const receipt = await deployTx.wait();
    const factoryAddress = receipt.contractAddress;

    console.log("âœ… BondFactory deployed at:", factoryAddress);
    console.log("âœ… Transaction hash:", receipt.hash);

    // Update .env file if present
    try {
      const envPath = ".env";
      if (fs.existsSync(envPath)) {
        let envContent = fs.readFileSync(envPath, 'utf8');
        if (/^BOND_FACTORY_ADDRESS=.*/m.test(envContent)) {
          envContent = envContent.replace(/^BOND_FACTORY_ADDRESS=.*/m, `BOND_FACTORY_ADDRESS="${factoryAddress}"`);
        } else {
          envContent += `\nBOND_FACTORY_ADDRESS="${factoryAddress}"\n`;
        }
        fs.writeFileSync(envPath, envContent);
        console.log("âœ… Updated .env with factory address");
      } else {
        console.log("â„¹ï¸  .env not found; please set BOND_FACTORY_ADDRESS manually in your deployment environment.");
      }
    } catch (e) {
      console.log("â„¹ï¸  Skipped updating .env:", e.message);
    }
    console.log(`ðŸ”— View on explorer: https://amoy.polygonscan.com/address/${factoryAddress}`);

    return factoryAddress;

  } catch (error) {
    console.error("âŒ Deployment failed:", error.message);
    throw error;
  }
}

deployContracts()
  .then(address => {
    console.log("\nðŸŽ‰ Deployment complete! Factory address:", address);
    process.exit(0);
  })
  .catch(error => {
    console.error("\nðŸ’¥ Failed:", error.message);
    process.exit(1);
  });
